(()=>{const e=new URLSearchParams(window.location.search).get("id"),t=document.querySelector(".category__heading"),a=JSON.parse(sessionStorage.getItem("productData")),c=document.querySelector(".category__products");t.textContent=e,a.forEach((t=>{if(t.category===e){const e=document.createElement("article");e.classList.add("category__product"),e.classList.add("container"),e.innerHTML=`\n          <picture class="category__image">\n            <source srcset="${t.image.desktop}" media="(min-width:64em)" type="image/webp" />\n            <source srcset="${t.image.tablet}" media="(min-width:48em)" type="image/webp" />\n            <img src="${t.image.mobile}" alt="" />\n          </picture>\n          <div class="category__info">\n            <small class="category--new">${t.new?"New Product":""}</small>\n            <h2 class="category__name">${t.name}</h2>\n            <p class="category__description">${t.description}</p>\n            <a href="./category-detail.html?id=${t.slug}" class="btn btn__primary category__link">See Product</a>\n          </div>`,c.appendChild(e)}}))})();